(function ($, window, document, undefined) {

  'use strict';

  $(function () {

    new Vue({
      el: '#js-vue',
      data: {
        message: 'the quick brown fox jumps over the lazy dog',
        bgPos: 0,
        colorSwitch: true
      },
      computed: {

      },
      methods: {
        updatePos: function(e) {
          this.bgPos = e.clientX
        },
        resetPos: function(e) {
          this.bgPos = 0
        },
        color: function(bool) {
          if (bool) return {backgroundColor: 'red'}
          else return {backgroundColor: 'white'}
        }
      }
    });

  });

})(jQuery, window, document);
